class Setting extends BaseSetting {

    public function getSlogan() {
        global $sf_culture;
        if ($sf_culture == 'en') {
            return $this->getEnSlogan();
        }
        else
            return $this->getCsSlogan();
    }

    public function getMesto() {
        global $sf_culture;
