            return $this->getCsMesto();
    }

    public function getPracovniDoba() {
        global $sf_culture;
        if ($sf_culture == 'en') {
            return $this->getEnPracovniDoba();
        }
        else
            return $this->getCsPracovniDoba();
    }

    public function getUlice() {
        global $sf_culture;
        if ($sf_culture == 'en') {
            return $this->getEnUlice();
        }
        else
            return $this->getCsUlice();
    }

}
